package org.example.themovingcompany.controller;

import jakarta.validation.Valid;
import org.example.themovingcompany.model.Order;
import org.example.themovingcompany.model.OrderRequestDTO;
import org.example.themovingcompany.model.Person;
import org.example.themovingcompany.model.enums.OrderStatus;
import org.example.themovingcompany.model.enums.PersonRole;
import org.example.themovingcompany.model.enums.ServiceType;
import org.example.themovingcompany.repository.PersonRepository;
import org.example.themovingcompany.service.OrderService;
import org.springframework.format.annotation.DateTimeFormat;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.time.LocalDate;
import java.util.List;
import java.util.Map;
import java.util.Optional;

@RestController
@RequestMapping("/api/orders") // Base path for all order endpoints.
public class OrderController {

    private final OrderService orderService;
    private final PersonRepository personRepository;

    // Constructor to inject dependencies
    public OrderController(OrderService orderService, PersonRepository personRepository) {
        this.orderService = orderService;
        this.personRepository = personRepository;
    }

    // GET /api/orders --> Returns all orders.
    @GetMapping
    public List<Order> getAllOrders() {
        return orderService.getAllOrders();
    }

    // GET /api/orders/{id} --> Returns one order by ID.
    @GetMapping("/{id}")
    public ResponseEntity<Order> getOrderById(@PathVariable Long id) {
        Optional<Order> order = orderService.getOrderById(id);
        return order.map(ResponseEntity::ok)
                .orElseGet(() -> ResponseEntity.notFound().build());

    }

    // GET /api/orders/by-consultant/{id} --> Returns all orders assigned to a specific consultant.
    @GetMapping("/by-consultant/{id}")
    public List<Order> getOrdersByConsultant(@PathVariable Long id) {
        return orderService.getOrdersByConsultant(id);
    }

    // GET /api/orders/by-customer/{id} --> Returns all orders placed by a specific customer.
    @GetMapping("/by-customer/{id}")
    public List<Order> getOrdersByCustomer(@PathVariable Long id) {
        return orderService.getOrdersByCustomer(id);
    }

    // GET /api/orders/by-status/{status} --> Returns all orders with a specific status.
    @GetMapping("/by-status/{status}")
    public List<Order> getOrdersByStatus(@PathVariable OrderStatus status) {
        return orderService.getOrdersByStatus(status);
    }

    @PostMapping
    public ResponseEntity<Order> createOrder(@Valid @RequestBody OrderRequestDTO request) {
        // Find the customer by ID (always required)
        Person customer = personRepository.findById(request.getCustomerId())
                .orElseThrow(() -> new IllegalArgumentException("Customer not found with id: " + request.getCustomerId()));

        // Check if consultantId is present
        Person consultant = null;

        if (request.getConsultantId() != null) {
            // Find the consultant by ID (if provided)
            consultant = personRepository.findById(request.getConsultantId())
                    .orElseThrow(() -> new IllegalArgumentException("Consultant not found with id: " + request.getConsultantId()));
        } else if (request.getParentOrderId() == null) {
            // If this is not a child order, consultantId is required
            throw new IllegalArgumentException("Consultant ID is required for parent orders");
        }

        // Manually build the order from the DTO
        Order order = new Order();
        order.setParentOrderId(request.getParentOrderId()); // May be null (no parent)
        order.setFromAddress(request.getFromAddress());
        order.setToAddress(request.getToAddress());
        order.setServiceType(ServiceType.valueOf(request.getServiceType()));
        order.setStartDate(LocalDate.parse(request.getStartDate()));
        order.setEndDate(LocalDate.parse(request.getEndDate()));
        order.setNote(request.getNote());
        order.setStatus(OrderStatus.valueOf(request.getStatus()));
        order.setCustomer(customer);

        if (consultant != null) {
            order.setConsultant(consultant); // Only set if present
        }

        // Validate that the person assigned as customer really has the CUSTOMER role
        if (!customer.getPersonRole().equals(PersonRole.CUSTOMER)) {
            throw new IllegalArgumentException("Person with ID " + customer.getId() + " is not a customer");
        }

        // If consultant is present, validate their role
        if (consultant != null && !consultant.getPersonRole().equals(PersonRole.CONSULTANT)) {
            throw new IllegalArgumentException("Person with ID " + consultant.getId() + " is not a consultant");
        }

        // Save and return the new order
        Order savedOrder = orderService.createOrder(order);
        return new ResponseEntity<>(savedOrder, HttpStatus.CREATED);
    }


    // GET /api/orders/by-service-type/{type} --> Returns all orders with a specific service type.
    @GetMapping("/by-service-type/{type}")
    public List<Order> getOrdersByServiceType(@PathVariable ServiceType type) {
        return orderService.getOrdersByServiceType(type);
    }


    // GET /api/orders/search?from=YYYY-MM-DD&to=YYYY-MM-DD --> Returns orders within a date range.
    @GetMapping("/search")
    public List<Order> searchOrdersByDateRange(
            @RequestParam("from") @DateTimeFormat(iso = DateTimeFormat.ISO.DATE) LocalDate from,
            @RequestParam("to") @DateTimeFormat(iso = DateTimeFormat.ISO.DATE) LocalDate to) {
        return orderService.searchOrdersByDateRange(from, to);
    }

    // PUT /api/orders/{id} --> Fully updates an existing order based on incoming DTO
    @PutMapping("/{id}")
    public ResponseEntity<String> updateOrder(
            @PathVariable Long id,
            @Valid @RequestBody OrderRequestDTO request) {
        try {
            // Call the service to perform the update
            orderService.updateOrder(id, request);

            // Return a success message (no entity serialization)
            return ResponseEntity.ok("Order updated successfully");
        } catch (IllegalArgumentException e) {
            return ResponseEntity.badRequest().body("Invalid input");
        }
    }



    // PATCH /api/orders/{id} --> Partially update an order.
    @PatchMapping("/{id}")
    public ResponseEntity<Order> patchOrder(
            @PathVariable Long id,
            @RequestBody Map<String, Object> updates) {

        try {
            Order updatedOrder = orderService.patchOrder(id, updates);
            return ResponseEntity.ok(updatedOrder);
        } catch (IllegalArgumentException e) {
            return ResponseEntity.badRequest().body(null);
        }
    }

    // DELETE /api/orders/{id} --> Deletes an order by ID.
    @DeleteMapping("/{id}")
    @ResponseStatus(HttpStatus.NO_CONTENT)
    public void deleteOrder(@PathVariable Long id) {
        orderService.deleteOrder(id);
    }
}

package org.example.themovingcompany.controller;

import jakarta.validation.Valid;
import org.example.themovingcompany.model.Person;
import org.example.themovingcompany.service.PersonService;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.List;
import java.util.Map;

@RestController                          // Exposes endpoints as REST.
@RequestMapping("/api/persons")          // Base path for this resource.
public class PersonController {

    private final PersonService personService;

    // Constructor injection of the service layer.
    public PersonController(PersonService personService) {
        this.personService = personService;
    }

    // GET /api/persons --> returns the full list.
    @GetMapping
    public List<Person> getAllPersons() {
        return personService.getAllPersons();
    }

    // GET /api/persons/by-role/{role} --> Returns persons filtered by role (CUSTOMER or CONSULTANT)
    @GetMapping("/by-role/{role}")
    public List<Person> getPersonsByRole(@PathVariable String role) {
        return personService.getPersonsByRole(role);
    }

    // GET /api/persons/by-role/CUSTOMER/archived --> Returns archived customers
    // This now uses the service instead of accessing the repository directly
    @GetMapping("/by-role/CUSTOMER/archived")
    public List<Person> getArchivedCustomers() {
        return personService.getArchivedCustomers();
    }

    // GET /api/persons/{id}  --> returns a single person or 404
    @GetMapping("/{id}")
    public ResponseEntity<Person> getPersonById(@PathVariable Long id) {
        return personService.getPersonById(id)
                .map(ResponseEntity::ok)          // 200 Ok if found.
                .orElseGet(() -> ResponseEntity.notFound().build()); // 404 if not.
    }

    // POST /api/persons --> creates a new person
    @PostMapping
    public Person createPerson(@Valid @RequestBody Person person) {
        return personService.createPerson(person);
    }

    // DELETE /api/persons/{id}  --> Deletes a person.
    @DeleteMapping("/{id}")
    @ResponseStatus(HttpStatus.NO_CONTENT)   // 204 No Content.
    public void deletePerson(@PathVariable Long id) {
        personService.deletePerson(id);
    }

    // PUT /api/persons/{id} --> Replace an existing person completely.
    @PutMapping("/{id}")
    public ResponseEntity<Person> updatePerson(@PathVariable Long id, @Valid @RequestBody Person updatedPerson) {
        // Call the service to update the person with new data.
        return personService.updatePerson(id, updatedPerson)
                .map(ResponseEntity::ok) // If update is successful, return 200 OK with the updated person.
                .orElseGet(() -> ResponseEntity.notFound().build()); // If person not found, return 404 Not Found.
    }

    // PATCH /api/persons/{id} --> Partially update an existing person.
    @PatchMapping("/{id}")
    public ResponseEntity<Person> patchPerson(
            @PathVariable Long id,
            @RequestBody Map<String, Object> updates) {

        try {
            Person updatedPerson = personService.patchPerson(id, updates);
            return ResponseEntity.ok(updatedPerson);
        } catch (IllegalArgumentException e) {
            return ResponseEntity.badRequest().body(null);
        }
    }
}

package org.example.themovingcompany.controller;


import org.springframework.core.env.Environment;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.HashMap;
import java.util.Map;

@RestController
public class ProfileController {

    private final Environment env;

    // Inject Spring's Environment to read active profiles
    public ProfileController(Environment env) {
        this.env = env;
    }

    // GET /active-profile --> Returns the currently active Spring profile.
    @GetMapping("/active-profile")
    public Map<String, String> getActiveProfile() {
        Map<String, String> response = new HashMap<>();
        String[] profiles = env.getActiveProfiles();

        // If no profile is active, fallback to "default"
        String activeProfile = profiles.length > 0 ? profiles[0] : "default";

        response.put("activeProfile", activeProfile);
        return response;
    }



}

package org.example.themovingcompany.exception;

// A simple structure to return errors in JSON format.
// Instead of showing a raw stack trace or a plain error message,
// we return a clean JSON object with:
// - the HTTP status code (like 400 or 404)
// - and a clear error message.
//
// This helps frontend apps, Postman users, or other APIs understand
// exactly what went wrong and how to handle the error.

public class ApiError {
    private int status;
    private String message;

    public ApiError(int status, String message) {
        this.status = status;
        this.message = message;
    }

    public int getStatus() {
        return status;
    }

    public String getMessage() {
        return message;
    }
}

package org.example.themovingcompany.exception;

import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

@ControllerAdvice // Intercepts exceptions from any controller
public class GlobalExceptionHandler {

    // Handles illegal arguments, like invalid dates
    @ExceptionHandler(IllegalArgumentException.class)
    public ResponseEntity<ApiError> handleIllegalArgument(IllegalArgumentException ex) {
        ApiError error = new ApiError(HttpStatus.BAD_REQUEST.value(), ex.getMessage());
        return new ResponseEntity<>(error, HttpStatus.BAD_REQUEST);
    }

    // It is possible to add more handlers for other exceptions (like 404, 422) later.
}

